import { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [timeSecond, setTimeSecond] = useState(0)
  const [timeMinute, setTimeMinute] = useState(0)
  const [paused, setPaused] = useState('false')

  useEffect(()=>{
    if(paused === 'false'){
      setTimeout(()=>{
        setTimeSecond(timeSecond+1)
       }, 1000)
       if(timeSecond === 60){
        setTimeMinute(timeMinute+1)
        setTimeSecond(0)
       }
    }
  },[timeSecond])

  const pause = () => {

    console.log(paused)
    if(pause === 'false'){
      console.log("Inside")
      console.log(paused)
      setPaused('true')
    }
    else{
      setPaused('false')
    }

  }
  return (
    <div className="App">
      Stopwatch <br></br>
      {timeMinute} : {timeSecond} <br></br>
      <button onClick={(e)=>pause()}>Pause</button>
    </div>
  );
}

export default App;
