import { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [timeSecond, setTimeSecond] = useState(0)
  const [timeMinute, setTimeMinute] = useState(0)
  const [paused, setPaused] = useState(true)

  useEffect(()=>{
    if(paused === false){
      setTimeout(()=>{
        setTimeSecond(timeSecond+1)
       }, 1000)
       if(timeSecond === 60){
        setTimeMinute(timeMinute+1)
        setTimeSecond(0)
       }
    }
  })

  const pause = () => {
    if(paused === false){
      setPaused(true)
    }
    else{
      setPaused(false)
    }
  }
  const reset = () => {
    setTimeSecond(0)
    setTimeMinute(0)
    setPaused(true)
  }
  return (
    <div className="App">
      Stopwatch <br></br>
      {timeMinute} : {timeSecond} <br></br>
      <button onClick={(e)=>pause()}>{paused === true ? "Start" : "Pause"}</button>
      <button onClick={(e)=>reset()}>Reset</button>
      <br></br>
      Count Down Timer <br></br>
    </div>
  );
}

export default App;
