import { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [timeSecond, setTimeSecond] = useState(0)
  const [timeMinute, setTimeMinute] = useState(0)
  const [paused, setPaused] = useState(true)
  const [countDownTime, setCountDownTime] = useState(0)
  const [starttimer, setStartimer] = useState(false)

  useEffect(()=>{
    if(paused === false){
      setTimeout(()=>{
        setTimeSecond(timeSecond+1)
       }, 1000)
       if(timeSecond === 60){
        setTimeMinute(timeMinute+1)
        setTimeSecond(0)
       }
    }

    if(starttimer== true){
      if(countDownTime >= 0){
        setTimeout(()=>{
          setCountDownTime(countDownTime-1)
         }, 1000)
      }
      else{
        setCountDownTime(0)
        setStartimer(false)
      }
      }


  })

  const pause = () => {
    if(paused === false){
      setPaused(true)
    }
    else{
      setPaused(false)
    }
  }
  const reset = () => {
    setTimeSecond(0)
    setTimeMinute(0)
    setPaused(true)
  }

  const startTimer = () =>{
    setStartimer(true)
  }
  return (
    <div className="App">
      Stopwatch <br></br>
      {timeMinute} : {timeSecond} <br></br>
      <button onClick={(e)=>pause()}>{paused === true ? "Start" : "Pause"}</button>
      <button onClick={(e)=>reset()}>Reset</button>
      <br></br>
      Count Down Timer <br></br>
      "Enter time in seconds"<br></br>
      <input type="number" onChange={(e) => {setCountDownTime(e.target.value)}}/>
      <button onClick={(e) => startTimer()}>Start Timer</button>
      <br></br>
      Time Left: {countDownTime}
    </div>
  );
}

export default App;
