import { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [timeMili,setTimeMili] = useState(0)
  const [timeSecond, setTimeSecond] = useState(0)
  const [timeMinute, setTimeMinute] = useState(0)
  const [paused, setPaused] = useState(true)

  useEffect(()=>{
    if(paused === false){
      setTimeout(()=>{
        setTimeMili(timeMili+1)
        console.log(timeMili)
       }, 100)
       if(timeMili === 1000){
        setTimeSecond(timeSecond+1)
       }
       if(timeSecond === 60){
        setTimeMinute(timeMinute+1)
        setTimeSecond(0)
       }
    }
  })

  const pause = () => {
    if(paused === false){
      setPaused(true)
    }
    else{
      setPaused(false)
    }

  }
  return (
    <div className="App">
      Stopwatch <br></br>
      {timeMinute} : {timeSecond} <br></br>
      <button onClick={(e)=>pause()}>{paused === true ? "Start" : "Pause"}</button>
    </div>
  );
}

export default App;
