import { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [time, setTime] = useState(0)
  useEffect(()=>{
    let interval = null;
    interval = setInterval(() => {
      setTime((time) => time + 10);
    }, 10);
    console.log(time)
  })
  return (
    <div className="App">
      Stopwatch
    </div>
  );
}

export default App;
